mapper.update:
UPDATE user SET userpwd = #{userpwd}, username = #{username}, useraddr = #{useraddr}, usertel = #{usertel}, useremail = #{useremail}, 
withdraw = #{withdraw}, userbirth = #{userbirth}, naverid = #{naverid}, kakaoid = #{kakaoid}, googleid = #{googleid}, usergen = #{usergen}, addrnum = #{addrnum}
WHERE userid = #{userid}

mapper.select:
SELECT u.userid, u.userpwd, u.username, m.mentorok, u.useraddr, u.usertel, u.useremail, u.userbirth, u.userpoint, u.usergen, u.addrnum FROM user u  
INNER JOIN mentor m ON u.userid = m.userid
WHERE u.userid = #{userid}
